mapper.select:
SELECT * FROM store_menu






                store_menu_name LIKE '%' || #{params.keyword} || '%'



                EXISTS (

                    SELECT 1 FROM dual

                    WHERE REGEXP_SUBSTR(store_menu_category, '[^/]+', 1, LEVEL) = #{params.keyword}

                    CONNECT BY REGEXP_SUBSTR(store_menu_category, '[^/]+', 1, LEVEL) IS NOT NULL
                )



                store_menu_name LIKE '%' || #{params.keyword} || '%'
                OR EXISTS (
                    SELECT 1 FROM dual
                    WHERE REGEXP_SUBSTR(store_menu_category, '[^/]+', 1, LEVEL) = #{params.keyword}
                    CONNECT BY REGEXP_SUBSTR(store_menu_category, '[^/]+', 1, LEVEL) IS NOT NULL
                )
